ejercicios.ejercicio:
select nombre,apellidos,telefono 
								from socios 
								order by apellidos;

select nombre,apellidos,telefono,cuota from socios 
								where nivel like "%MEDIO%" and cuota > 3 
								order by localidad and apellidos;

select * from socios 
								where localidad in ("Arnedo", "Calahorra", "Alfaro") 
								order by localidad;

select * from socios 
								where nombre like "A%" 
								order by localidad;
